import React from 'react';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Button, Input } from '@shadcn/ui'; // Adjust based on your library

const Signup = () => {
  const navigate = useNavigate();

  const formik = useFormik({
    initialValues: {
      username: '',
      email: '',
      password: '',
      confirmPassword: '',
    },
    validationSchema: Yup.object({
      username: Yup.string().required('Required'),
      email: Yup.string().email('Invalid email address').required('Required'),
      password: Yup.string().required('Required'),
      confirmPassword: Yup.string()
        .oneOf([Yup.ref('password'), null], 'Passwords must match')
        .required('Required'),
    }),
    onSubmit: async (values) => {
      try {
        const response = await axios.post('/api/auth/signup', {
          username: values.username,
          email: values.email,
          password: values.password,
        });
        alert(response.data.msg); // Show success message
        navigate('/login'); // Redirect to login after successful signup
      } catch (error) {
        console.error(error);
        alert('Signup failed!'); // Replace with toast notification
      }
    },
  });

  return (
    <div className="flex items-center justify-center h-screen bg-gray-100">
      <form onSubmit={formik.handleSubmit} className="bg-white p-8 rounded shadow-md">
        <h2 className="text-2xl mb-4">Sign Up</h2>
        <Input
          type="text"
          name="username"
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
          value={formik.values.username}
          placeholder="Username"
          className="mb-4"
        />
        {formik.touched.username && formik.errors.username && <div>{formik.errors.username}</div>}
        <Input
          type="email"
          name="email"
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
          value={formik.values.email}
          placeholder="Email"
          className="mb-4"
        />
        {formik.touched.email && formik.errors.email && <div>{formik.errors.email}</div>}
        <Input
          type="password"
          name="password"
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
          value={formik.values.password}
          placeholder="Password"
          className="mb-4"
        />
        {formik.touched.password && formik.errors.password && <div>{formik.errors.password}</div>}
        <Input
          type="password"
          name="confirmPassword"
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
          value={formik.values.confirmPassword}
          placeholder="Confirm Password"
          className="mb-4"
        />
        {formik.touched.confirmPassword && formik.errors.confirmPassword && <div>{formik.errors.confirmPassword}</div>}
        <Button type="submit" className="bg-blue-500 text-white">Sign Up</Button>
        <p className="mt-2">
          Already have an account? <a href="/login" className="text-blue-500">Login</a>
        </p>
      </form>
    </div>
  );
};

export default Signup;
