import React, { useState } from 'react';
import { Button, Input, FormControl, FormLabel, FormErrorMessage } from '@chakra-ui/react'; 
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Signup = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    // Basic validation
    if (!username || !password || !confirmPassword) {
      setError('All fields are required');
      return;
    }
    if (password !== confirmPassword) {
      setError('Passwords do not match');
      return;
    }

    try {
      await axios.post('http://localhost:4000/api/auth/signup', {
        username,
        password,
        confirmPassword,
      });

      setSuccess('Signup successful! You can now log in.');
      setTimeout(() => {
        navigate('/login'); // Redirect to login after successful signup
      }, 2000);
    } catch (error) {
      console.error(error.response ? error.response.data : error.message); // Log the actual error message
      setError('Signup failed! Please try again.');
    }
  };

  return (
    <div className="flex items-center justify-center h-screen bg-gray-100">
      <form onSubmit={handleSubmit} className="bg-white p-8 rounded shadow-md">
        <h2 className="text-2xl mb-4">Sign Up</h2>
        <FormControl isInvalid={!!error}>
          <FormLabel>Username</FormLabel>
          <Input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Username"
            className="mb-4"
          />

          <FormLabel>Password</FormLabel>
          <Input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Password"
            className="mb-4"
          />
          <FormLabel>Confirm Password</FormLabel>
          <Input
            type="password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            placeholder="Confirm Password"
            className="mb-4"
          />
          { error && <FormErrorMessage>{error}</FormErrorMessage>}
          { success && <div className="text-green-500">{success}</div>}
          <Button type="submit" colorScheme="blue">Sign Up</Button>
          <p className="mt-2">
            Already have an account? <a href="/login" className="text-blue-500">Login</a>
          </p>
        </FormControl>
      </form>
    </div>
  );
};

export default Signup;
